import React from "react"

class UserList extends React.Component{
  constructor(props){
    super(props);
    this.state = {nameSort:0,sexSort:0, rankSort: 0, dateSort: 0};
  }
  handleNameClick = () => {
    if(this.state.nameSort === 0){
      this.props.getList(this.props.search,"Name",1,this.props.page);
    }

  }
  render() {
    // console.log(this.props);
    return (
      <div>
      <table>
        <thead>
          <tr>
            <th>avatar</th>
            <th onClick={this.handleNameClick}>name</th>
            <th onClick={this.handleSexClick}>sex</th>
            <th onClick={this.handleRankClick}>rank</th>
            <th onClick={this.handleDateClick}>StartDate</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Superior</th>
            <th onClick={this.handleSexClick}>DS</th>
          </tr>
        </thead>
        <tbody>
          {this.props.userList.data.map((user) => {
            return(
              <tr key={user._id}>
                <td>{(user.Avatar !== "") && <img src={require(`../../../../src/server/${user.Avatar}`)} alt=""/>}</td>
                <td>{user.Name}</td>
                <td>{user.Sex}</td>
                <td>{user.Rank}</td>
                <td>{user.StartDate}</td>
                <td>{user.Phone}</td>
                <td>{user.Email}</td>
                <td onClick={() => this.props.getListOne(user.Superior)}>{this.props.allList.data.find((userId) => {return userId.id === user._id}).name}</td>
                <td onClick={() => this.props.getDS(user._id)}>{user.DS.length}</td>
                <td><button onClick={() => {this.props.getOneUser(user._id, this.props.history);}}>edit</button></td>
                <td><button onClick={() => {this.props.deleteOneUser(user)}}>delete</button></td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <button onClick={() => this.props.history.push('/create')}>create</button>
    </div>
    );
  }
}
export default UserList;
