import React from "react";
import * as actions from "../../redux/actions/actions"
import {connect} from 'react-redux';
const user = {
  Avatar: "http://sadoifhasdfiuhasdfiuh.com",
  Name: "test 123",
  Sex: "Male",
  Rank: "Major",
  StartDate: "Sat Jan 05 2019 00:00:00 GMT-0800 (GMT-08:00)",
  Phone: "2026581651",
  Email: "sdfasdfsd",
  Superior: "5dc3480ce49a144b12f4ad73"
};
class Create extends React.Component{
  constructor(props){
    super(props);
    this.state={user:{Avatar: "",Name: "",Sex: "",Rank: "",StartDate: "",Phone: "",Email: "",Superior: ""}};
  }
  handleAvatar = (e) => {
    this.setState({user:{...this.state.user, Avatar: e.target.value}});
  }
  handleName = (e) => {
    this.setState({user:{...this.state.user, Name: e.target.value}});
  }
  handleSex = (e) => {
    this.setState({user:{...this.state.user, Sex: e.target.value}});
  }
  handleRank = (e) => {
    this.setState({user:{...this.state.user, Rank: e.target.value}});
  }
  handleStartDate = (e) => {
    this.setState({user:{...this.state.user, StartDate: e.target.value}});
  }
  handlePhone = (e) => {
    this.setState({user:{...this.state.user, Phone: e.target.value}});
  }
  handleEmail = (e) => {
    this.setState({user:{...this.state.user, Email: e.target.value}});
  }
  handleSuperior = (e) => {
    this.setState({user:{...this.state.user, Superior: e.target.value}});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.createOne(this.state.user);
    this.props.history.push('/');
  }
  render() {
    return (
    <div>
      <button onClick={() => this.props.createOne(user)}></button>
      <form onSubmit={this.handleSubmit}>
        <label>avatar</label>
        <input value={this.state.user.Avatar} onChange={this.handleAvatar}/>
        <label>Name</label>
        <input value={this.state.user.Name} onChange={this.handleName}/>
        <label>Sex</label>
        <input value={this.state.user.Sex} onChange={this.handleSex}/>
        <label>Rank</label>
        <input value={this.state.user.Rank} onChange={this.handleRank}/>
        <label>StartDate</label>
        <input value={this.state.user.StartDate} onChange={this.handleStartDate}/>
        <label>Phone</label>
        <input value={this.state.user.Phone} onChange={this.handlePhone}/>
        <label>Email</label>
        <input value={this.state.user.Email} onChange={this.handleEmail}/>
        <label>Superior</label>
        <input value={this.state.user.Superior} onChange={this.handleSuperior}/>
        <input type="submit"/>
      </form>
    </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    userList: state
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
    createOne: (user) => {
      dispatch(actions.createOne(user));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Create);
