import React from "react"

class UserList extends React.Component{
  constructor(props){
    super(props);
    this.state = {};
  }
  render() {
    // console.log(this.props);
    return (
      <div>
      <table>
        <thead>
          <tr>
            <th>avatar</th>
            <th onClick={this.handleLastnameClick}>name</th>
            <th onClick={this.handleAgeClick}>sex</th>
            <th onClick={this.handleSexClick}>rank</th>
            <th onClick={this.handleSexClick}>StartDate</th>
            <th onClick={this.handleSexClick}>Phone</th>
            <th onClick={this.handleSexClick}>Email</th>
            <th>Superior</th>
            <th onClick={this.handleSexClick}>DS</th>
          </tr>
        </thead>
        <tbody>
          {this.props.userList.data.map((user) => {
            return(
              <tr key={user._id}>
                <td>{(user.Avatar !== "") && <img src={require(`../../../../src/server/${user.Avatar}`)} alt=""/>}</td>
                <td>{user.Name}</td>
                <td>{user.Sex}</td>
                <td>{user.Rank}</td>
                <td>{user.StartDate}</td>
                <td>{user.Phone}</td>
                <td>{user.Email}</td>
                <td onClick={() => this.props.getListOne(user.Superior)}>{user.SuperiorName}</td>
                <td onClick={() => this.props.getDS(user._id)}>{user.DS.length}</td>
                <td><button onClick={() => {this.props.getOneUser(user._id, this.props.history);}}>edit</button></td>
                <td><button onClick={() => {this.props.deleteOneUser(user)}}>delete</button></td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <button onClick={() => this.props.history.push('/create')}>create</button>
    </div>
    );
  }
}
export default UserList;
