import React from "react";

class InputForm extends React.Component {
  constructor(props){
    super(props);
    this.state={user:{_id:this.props.userdata._id, Avatar: this.props.userdata.Avatar, Name: this.props.userdata.Name,
      Sex: this.props.userdata.Sex, Rank: this.props.userdata.Rank, StartDate: this.props.userdata.StartDate,
      Phone: this.props.userdata.Phone, Email: this.props.userdata.Email, Superior: this.props.userdata.Superior},oldSuperior:this.props.userdata.Superior};
  }
  handleAvatar = (e) => {
    this.setState({user:{...this.state.user, Avatar: e.target.value}});
  }
  handleName = (e) => {
    this.setState({user:{...this.state.user, Name: e.target.value}});
  }
  handleSex = (e) => {
    this.setState({user:{...this.state.user, Sex: e.target.value}});
  }
  handleRank = (e) => {
    this.setState({user:{...this.state.user, Rank: e.target.value}});
  }
  handleStartDate = (e) => {
    this.setState({user:{...this.state.user, StartDate: e.target.value}});
  }
  handlePhone = (e) => {
    this.setState({user:{...this.state.user, Phone: e.target.value}});
  }
  handleEmail = (e) => {
    this.setState({user:{...this.state.user, Email: e.target.value}});
  }
  handleSuperior = (e) => {
    this.setState({user:{...this.state.user, Superior: e.target.value}});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.updateOne(this.state.user, this.state.oldSuperior, this.state.user.Superior, this.props.history);
  }
  render() {
    console.log(this.state.user);
    return (
    <div>
      <form onSubmit={this.handleSubmit}>
        <label>avatar</label>
        <input value={this.state.user.Avatar} onChange={this.handleAvatar}/>
        <label>Name</label>
        <input value={this.state.user.Name} onChange={this.handleName}/>
        <label>Sex</label>
        <input value={this.state.user.Sex} onChange={this.handleSex}/>
        <label>Rank</label>
        <input value={this.state.user.Rank} onChange={this.handleRank}/>
        <label>StartDate</label>
        <input value={this.state.user.StartDate} onChange={this.handleStartDate}/>
        <label>Phone</label>
        <input value={this.state.user.Phone} onChange={this.handlePhone}/>
        <label>Email</label>
        <input value={this.state.user.Email} onChange={this.handleEmail}/>
        <label>Superior</label>
        <select onChange={this.handleSuperior}>
          {this.props.nameIdList.map((pair,index) => {
            return (<option key={index} value={pair.id} >{pair.name}</option>);
          })}
        </select>
        <input type="submit"/>
      </form>
    </div>
    );
  }
}
export default InputForm;
