import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
const OneUser = (props) => {
  return (
    <tr key={props.user._id}>
                  <td>{user.Avatar}</td>
                  <td>{user.Name}</td>
                  <td>{user.Sex}</td>
                  <td>{user.Rank}</td>
                  <td>{user.startDate}</td>
                  <td>{user.Phone}</td>
                  <td>{user.Email}</td>
                  <td onClick={() => this.props.getListOne(user.Superior)}>{user.Superior === "" ? "":this.props.userList.data.find((inuser) => {
                         return inuser._id === user.Superior;
                  }).Name}</td>
                  <td onClick={() => this.props.getDS(user._id)}>{user.DS.length}</td>
                  <td><button onClick={() => {this.props.getUser(user._id);}}>edit</button></td>
                  <td><button onClick={() => {this.props.deleteOneUser(user._id)}}>delete</button></td>
                </tr>
  );
}
const mapStateToProps = state => {
  return {
    userList: state
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(OneUser);
