import React from "react";
import {withRouter, Router, Route, Switch} from "react-router-dom";
import Create from "./create";
import Home from "./Home";
import Edit from "./edit";
import { createBrowserHistory } from "history";
const WithRouterCreate = withRouter(Create);
const WithRouterEdit = withRouter(Edit);
const history = createBrowserHistory();
class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {memory:{search:"",sortArea:"",sortFunc:"",page:1}};
  }
  render() {
    this.setState({memory:{...this.props.memory,page: this.props.memory.page + 1}});
    return (
      <Router history={history}>
        <Switch>
        <Route
            exact={true}
            path="/"
            render={() => <Home 
              memory={this.state.memory} 
              setMemory={this.setState}
              />}
        />
          {/* <Route exact={true} path="/" component={Home} /> */}
          <Route path="/create" component={WithRouterCreate} />
          <Route path="/edit/:id" component={WithRouterEdit} />
        </Switch>
      </Router>
    );
  }
}
export default App;
