import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
import UserList from "./UserList"
import {withRouter} from 'react-router-dom';
const WithRouterList = withRouter(UserList);
var page = 1, number = 0;
class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {search:""};
  }
  componentDidMount() {
    this.refs.myscroll.addEventListener("scroll", () => {
      if (
        this.refs.myscroll.scrollTop + this.refs.myscroll.clientHeight >=
        this.refs.myscroll.scrollHeight
      ) {
        this.loadMore();
      }
    });
    this.props.getAllList();
    this.props.clear();
    this.props.getList("","","",1);
  }
  loadMore = () => {
    number = this.props.allList.length;
    if(page * 4 < number){
      page = page+1;
      this.props.getList("","","",page);
      console.log(page+"in mount");
    }
  }
  handleSearch = (e) => {
    this.setState({search:e.target.value});
  }
  render() {
    console.log(number);
    return (
      <div
        className="App"
        ref="myscroll"
        style={{ height: "200px", overflow: "auto" }}
      >
        <input type="text" value={this.state.search} onChange={this.handleSearch}></input>
        <button onClick={() => this.props.getList(this.state.search,"","",page)}>search</button>
        <button onClick={() => {this.props.getList("","","",1);page=1}}>reset</button>
        <WithRouterList
          search={this.state.search}
          page={page}
          userList={this.props.userList}
          getListOne={this.props.getListOne}
          getDS={this.props.getDS}
          deleteOneUser={this.props.deleteOneUser}
          getOneUser={this.props.getOneUser}
          getList={this.props.getList}
        />
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    userList: state.userListReducer,
    allList: state.nameIdReducer
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
    deleteOneUser: (user) => {
      dispatch(actions.deleteOneUser(user));
    },
    clear: () => {
      dispatch(actions.clear());
    },
    getOneUser: (id,history) => {
      dispatch(actions.getOneUser(id,history))
    },
    getAllList: () => {
      dispatch(actions.getAllList());
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
