import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
import {Provider} from 'react-redux';
import {userstore} from "../../redux/store/store"
class OneUser extends React.Component{
  constructor(props){
    super(props);
    this.state = {Superior:this.props.user.Superior};
  }
  componentDidMount() {
    this.props.clear()
    if(this.state.Superior !== ""){
      this.props.getOneUser(this.state.Superior);
    }
  }
  render() {
    console.log(this.props.officeruser);
    return (
      <Provider store={userstore}>
        <tr key={this.props.user._id}>
          <td>{this.props.user.Avatar}</td>
          <td>{this.props.user.Name}</td>
          <td>{this.props.user.Sex}</td>
          <td>{this.props.user.Rank}</td>
          <td>{this.props.user.startDate}</td>
          <td>{this.props.user.Phone}</td>
          <td>{this.props.user.Email}</td>
          <td onClick={() => this.props.getOneUser()}>{this.props.officeruser.data.Name}</td>
          <td onClick={() => this.props.getDS()}>{this.props.user.DS.length}</td>
          {/* <td><button onClick={() => {this.props.getUser(user._id);}}>edit</button></td>
          <td><button onClick={() => {this.props.deleteOneUser(user._id)}}>delete</button></td> */}
        </tr>
        </Provider>
    );
  }
}
const mapStateToProps = state => {
  return {
    officeruser: state.userReducer
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getOneUser: (Id) => {
      dispatch(actions.getOneUser(Id));
    },
    clear: () => {
      dispatch(actions.userClear());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(OneUser);
