import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
import UserList from "./UserList"
import {withRouter} from 'react-router-dom';
import InfiniteScroll from 'react-infinite-scroller';
const WithRouterList = withRouter(UserList);
var page = 0;
const style = {height:"300px",overflow:"auto"}
class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {page:1};
  }
  componentDidMount() {
    this.props.clear();
    this.props.getList("","","",page);
    console.log(page+"in mount");
  }

  render() {
    return (
      <div style={style}>
          <InfiniteScroll
            pageStart={0}
            loadMore={() => {page++; console.log(page);}}
            hasMore={page<5}
            loader={<div className="loader" key={0}>Loading ...</div>}
            threshold={400}
            useWindow={false}
            >
          </InfiniteScroll>
          <WithRouterList
              page={page}
              userList={this.props.userList}
              getListOne={this.props.getListOne}
              getDS={this.props.getDS}
              deleteOneUser={this.props.deleteOneUser}
              getOneUser={this.props.getOneUser}
            />
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    userList: state.userListReducer
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
    deleteOneUser: (user) => {
      dispatch(actions.deleteOneUser(user));
    },
    clear: () => {
      dispatch(actions.clear());
    },
    getOneUser: (id,history) => {
      dispatch(actions.getOneUser(id,history))
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
