import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
import OneUser from "./Oneuser"
import {Provider} from 'react-redux';
import {userstore} from "../../redux/store/store"
class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    this.props.getList("","","",3);
  }
  render() {
    // console.log(this.props);
    return (
      <Provider store={userstore}>
        <div>
          <table>
            <thead>
              <tr>
                <th onClick={this.handleFirstnameClick}>avatar</th>
                <th onClick={this.handleLastnameClick}>name</th>
                <th onClick={this.handleAgeClick}>sex</th>
                <th onClick={this.handleSexClick}>rank</th>
                <th onClick={this.handleSexClick}>StartDate</th>
                <th onClick={this.handleSexClick}>Phone</th>
                <th onClick={this.handleSexClick}>Email</th>
                <th onClick={this.handleSexClick}>Superior</th>
                <th onClick={this.handleSexClick}>DS</th>
              </tr>
            </thead>
            <tbody>
              {this.props.userList.data.map((user) => {
                return(
                  <OneUser key={user._id} user={user}></OneUser>
                );
              })}
            </tbody>
          </table>
        </div>
      </Provider>
    );
  }
}
const mapStateToProps = state => {
  return {
    userList: state.userListReducer
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
