import React from "react"
import {connect} from 'react-redux';
import * as actions from "../../redux/actions/actions"
class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    this.props.getList("","","",3);
  }

  render() {
    console.log(this.props.userList.data);
    return (
      <div>
        <table>
          <thead>
            <tr>
              <th onClick={this.handleFirstnameClick}>avatar</th>
              <th onClick={this.handleLastnameClick}>name</th>
              <th onClick={this.handleAgeClick}>sex</th>
              <th onClick={this.handleSexClick}>rank</th>
              <th onClick={this.handleSexClick}>StartDate</th>
              <th onClick={this.handleSexClick}>Phone</th>
              <th onClick={this.handleSexClick}>Email</th>
              <th onClick={this.handleSexClick}>Superior</th>
              <th onClick={this.handleSexClick}>DS</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userList.data.map((user) => {
              return(
                <tr key={user._id}>
                  <td>{user.Avatar}</td>
                  <td>{user.Name}</td>
                  <td>{user.Sex}</td>
                  <td>{user.Rank}</td>
                  <td>{user.startDate}</td>
                  <td>{user.Phone}</td>
                  <td>{user.Email}</td>
                  <td onClick={() => this.props.getListOne(user.Superior)}>{user.SuperiorName}</td>
                  <td onClick={() => this.props.getDS(user._id)}>{user.DS.length}</td>
                  {/* <td><button onClick={() => {this.props.getUser(user._id);}}>edit</button></td>
                  <td><button onClick={() => {this.props.deleteOneUser(user._id)}}>delete</button></td> */}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    userList: state
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getList: (search,sortArea,sortFunc,page) => {
      dispatch(actions.getList(search,sortArea,sortFunc,page));
    },
    getListOne: (Id) => {
      console.log(Id === "");
      dispatch(actions.getListOne(Id));
    },
    getDS: (OfficerId) => {
      dispatch(actions.getDS(OfficerId));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
